import React, { useState, useEffect } from 'react';
import { Button, Badge } from '../UI';
import { useNotification } from '../../contexts/NotificationContext';

const NotificationPanel = ({ isOpen, onClose }) => {
  const { notifications, markAsRead, clearAll } = useNotification();
  const [filter, setFilter] = useState('all');

  const filteredNotifications = notifications.filter(notification => {
    if (filter === 'unread') return !notification.read;
    if (filter === 'read') return notification.read;
    return true;
  });

  const unreadCount = notifications.filter(n => !n.read).length;

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'order':
        return 'üì¶';
      case 'task':
        return '‚úÖ';
      case 'user':
        return 'üë§';
      case 'system':
        return '‚öôÔ∏è';
      default:
        return 'üîî';
    }
  };

  const getNotificationColor = (type) => {
    switch (type) {
      case 'order':
        return 'blue';
      case 'task':
        return 'green';
      case 'user':
        return 'purple';
      case 'system':
        return 'gray';
      default:
        return 'blue';
    }
  };

  const formatTime = (timestamp) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInMinutes = Math.floor((now - time) / (1000 * 60));

    if (diffInMinutes < 1) return '≈ûimdi';
    if (diffInMinutes < 60) return `${diffInMinutes} dakika √∂nce`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)} saat √∂nce`;
    return `${Math.floor(diffInMinutes / 1440)} g√ºn √∂nce`;
  };

  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-25 z-40"
        onClick={onClose}
      />

      {/* Panel */}
      <div className="fixed top-0 right-0 h-full w-96 bg-white shadow-xl z-50 transform transition-transform duration-300 ease-in-out">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b">
          <div className="flex items-center space-x-2">
            <h2 className="text-lg font-semibold text-gray-900">Bildirimler</h2>
            {unreadCount > 0 && (
              <Badge variant="danger" size="sm">
                {unreadCount}
              </Badge>
            )}
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Filters */}
        <div className="p-4 border-b">
          <div className="flex space-x-2">
            <Button
              size="sm"
              variant={filter === 'all' ? 'primary' : 'outline'}
              onClick={() => setFilter('all')}
            >
              T√ºm√º ({notifications.length})
            </Button>
            <Button
              size="sm"
              variant={filter === 'unread' ? 'primary' : 'outline'}
              onClick={() => setFilter('unread')}
            >
              Okunmamƒ±≈ü ({unreadCount})
            </Button>
            <Button
              size="sm"
              variant={filter === 'read' ? 'primary' : 'outline'}
              onClick={() => setFilter('read')}
            >
              Okunmu≈ü
            </Button>
          </div>
        </div>

        {/* Actions */}
        {notifications.length > 0 && (
          <div className="p-4 border-b">
            <div className="flex space-x-2">
              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  notifications.forEach(n => {
                    if (!n.read) markAsRead(n.id);
                  });
                }}
                disabled={unreadCount === 0}
              >
                T√ºm√ºn√º Okundu ƒ∞≈üaretle
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={clearAll}
              >
                T√ºm√ºn√º Temizle
              </Button>
            </div>
          </div>
        )}

        {/* Notifications List */}
        <div className="flex-1 overflow-y-auto">
          {filteredNotifications.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-64 text-gray-500">
              <svg className="w-12 h-12 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5-5-5h5v-12" />
              </svg>
              <p className="text-center">
                {filter === 'all' ? 'Hen√ºz bildirim yok' : 
                 filter === 'unread' ? 'Okunmamƒ±≈ü bildirim yok' : 'Okunmu≈ü bildirim yok'}
              </p>
            </div>
          ) : (
            <div className="divide-y">
              {filteredNotifications.map((notification) => (
                <div
                  key={notification.id}
                  className={`p-4 hover:bg-gray-50 cursor-pointer transition-colors ${
                    !notification.read ? 'bg-blue-50' : ''
                  }`}
                  onClick={() => markAsRead(notification.id)}
                >
                  <div className="flex items-start space-x-3">
                    <div className={`flex-shrink-0 w-8 h-8 rounded-full bg-${getNotificationColor(notification.type)}-100 flex items-center justify-center`}>
                      <span className="text-sm">{getNotificationIcon(notification.type)}</span>
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <p className={`text-sm font-medium text-gray-900 ${
                          !notification.read ? 'font-semibold' : ''
                        }`}>
                          {notification.title}
                        </p>
                        {!notification.read && (
                          <div className="w-2 h-2 bg-blue-600 rounded-full flex-shrink-0"></div>
                        )}
                      </div>
                      <p className="text-sm text-gray-600 mt-1">
                        {notification.message}
                      </p>
                      <p className="text-xs text-gray-400 mt-2">
                        {formatTime(notification.timestamp)}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default NotificationPanel;
